import React from "react";
import "./ExpenseForm.css";

function ExpenseForm(props) {

  const[enteredTitle, setTitle] = React.useState("");
  const[enteredAmount, setAmount] = React.useState("");
  const[enteredDate, setDate] = React.useState("");

  const titleChangeHandler = (event) => {
    setTitle(event.target.value);
  }

  const amountChangeHandler = (event) => {
    setAmount(event.target.value);
  }

  const dateChangeHandler = (event) => {
    setDate(event.target.value);
  }

  const submitHandler = (event) => {
    event.preventDefault();

    const expenseData ={
      title : enteredTitle,
      amount : enteredAmount,
      date : new Date(enteredDate)
    }

    props.onSaveExpenseForm(expenseData);
    setTitle("");
    setAmount("");
    setDate("");
  }

  // const [userInput, setUserInput] = React.useState({
  //   title : "",
  //   amount : "",
  //   date : ""
  // });

  // const titleChangeHandler = (event) => {
  //   setUserInput((prevState) => {
  //     return { ...prevState, title : event.target.value}
  //   });
  // };


  return(
  <form onSubmit={submitHandler}>
    <div className="new-expense__controls">
      <div className="new-expense__control">
        <label>Title</label>
        <input type="text" value={enteredTitle} onChange={titleChangeHandler} />
      </div>
      <div className="new-expense__control">
        <label>Amount</label>
        <input type="number" value={enteredAmount} min= "1" step = "1" onChange={amountChangeHandler}/>
      </div>
      <div className="new-expense__control">
        <label>Date</label>
        <input type="date" value={enteredDate} min="2019-01-01" max="2022-12-31" onChange={dateChangeHandler}/>
      </div>
    </div>
    <div className="new-expense__actions">
    <button type="submit">Add Expense</button>
    </div>
  </form>
  )
}

export default ExpenseForm;
